/*
    Just helps you manually verify that a type is what it says it is.
    For example,

        check<number>("not a number"); //Compile time error

        check<boolean>(true); //OK

        check<SomeComplexObject>({
            x : "test",
            y : {
                z : 3.141
            },
        });
*/
export function check<T> (x : T) : T {
    return x;
}
