export function rename (name : string) {
    //This is really just a hack
    //Function names are read-only and can not be changed.
    //Unless you use defineProperty() to force a rewrite
    function renamer<CtorT extends {new(...args:any[]):object}> (ctor : CtorT) {
        Object.defineProperty(ctor, "name", {
            value : name,
        });
        return ctor;
    };
    return renamer;
}
